package org.devbuild.algoservice.sort;import org.devbuild.algoservice.dto.Node;import java.util.List;public interface SortService {    public List<Node> sort(List<Node> nodes) throws InterruptedException;    public void mergeSort(List<Node> nodes, int start , int end, long timeInMills) throws InterruptedException;    void populateNodeData(List<Node> nodes);    Node createTreeFromArray(List<Node> nodes, int i);    void swap(Node node1, Node node2, boolean leftRoot);    void maxHeap(List<Node> nodes, int i, int n) throws InterruptedException;    List<Node> heapSort(List<Node> nodes, long timeInMills) throws InterruptedException;    void bubbleSort(List<Node> nodes, long timeInMills) throws InterruptedException;}